// Luma Fertility - Appointment Confirmation (Real-time)
// Template: Appt confirmation with date, time, address, location link
// Condition: Only send if reason_for_visit = 'new consult'


// ===== HELPER FUNCTIONS =====

function digitsOnlyPhone(p) {
  if (!p) return '';
  let s = String(p).trim().replace(/[^\d]/g, '');
  if (s.startsWith('0')) s = s.replace(/^0+/, '');
  if (s.length === 10) s = '91' + s;
  return s;
}

function toDisplayTime(timeStr) {
  try {
    if (!timeStr) return '';
    if (/[ap]m$/i.test(timeStr.trim())) return timeStr.trim();

    const parts = timeStr.split(':').map(n => parseInt(n, 10));
    const hh = parts[0];
    const mm = parts[1] ?? 0;

    if (Number.isNaN(hh) || Number.isNaN(mm)) return timeStr;

    const h12 = ((hh + 11) % 12) + 1;
    const ampm = hh >= 12 ? 'PM' : 'AM';
    return `${h12}:${String(mm).padStart(2, '0')} ${ampm}`;
  } catch (e) {
    return timeStr || '';
  }
}

function formatDate(dateStr) {
  try {
    if (!dateStr) return '';
    const date = new Date(dateStr);
    const day = String(date.getDate()).padStart(2, '0');
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const year = date.getFullYear();
    return `${year}-${month}-${day}`;
  } catch (e) {
    return dateStr;
  }
}

function getDayName(dateStr) {
  try {
    if (!dateStr) return '';
    const date = new Date(dateStr);
    const days = ['Sunday', 'Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
    return days[date.getDay()];
  } catch (e) {
    return '';
  }
}

// ===== CONDITIONAL CHECK =====
/// ===== CONDITIONAL CHECK =====
// Only send if visit.reasonName = 'New Patient'
const reasonName =
  payload.visit?.reasonName ||
  payload.appt?.reasonName ||       // fallback (if some feeds put it on appt)
  payload.reasonName ||
  '';

const reasonNameNormalized = String(reasonName || '').trim().toLowerCase();

