  payload.patient?.phone ||
  payload.appt?.patientPhone ||
  payload.patientPhone ||
  payload.phone ||
  '';

const to_contact = digitsOnlyPhone(phone);
if (!to_contact) return null; // No phone â†’ don't send

// Patient name
