  payload.patient?.uhid ||
  payload.uhid ||
  payload.patient?.mrn?.documentNumber ||
  payload.patientMRN ||
  '';

// Appointment date/time
const apptDate = 
  payload.appt?.apptDate || 
  payload.appt?.fromDate || 
  payload.apptDate || 
  payload.fromDate || 
  payload.appointmentDate ||
  '';

const apptTime = 
  payload.appt?.apptTime || 
  payload.appt?.fromTime || 
  payload.apptTime || 
  payload.fromTime || 
  payload.appointmentTime ||
  '';

// Format date and time
const dateFormatted = formatDate(apptDate);
const dayName = getDayName(apptDate);
const timeFormatted = toDisplayTime(apptTime);

// Address and location
const address = 
  payload.appt?.address ||
  payload.address ||
  payload.entityAddress ||
  context.entityAddress ||
  'Luma Fertility, 2nd Floor, Mansionz One, Linking Rd, near Copper Chimney Restaurant, Bandra West, Mumbai, Maharashtra 400050';

const locationLink = 
  payload.appt?.locationLink ||
  payload.locationLink ||
  payload.appt?.mapsLink ||
  'https://maps.app.goo.gl/o71rYTsBtLYRqxu29?g_st=aw';

// ===== QIKCHAT TEMPLATE MAPPING =====

