// Calculate D+5hrs (5 hours after patient registration)
// For PATIENT_REGISTERED events, use the datetime field

const datetime = event?.datetime || event?.visit?.datetime;

if (!datetime) {
  throw new Error('Missing datetime for D+5hrs welcome message');
}

// Parse datetime format: "13/02/2026 01:12 PM"
// Format: DD/MM/YYYY hh:mm AM/PM
function parseDatetime(dtStr) {
  const parts = dtStr.split(' ');
  const dateParts = parts[0].split('/');
  const timeParts = parts[1].split(':');
  const ampm = parts[2];

  const day = parseInt(dateParts[0], 10);
  const month = parseInt(dateParts[1], 10) - 1; // JS months are 0-indexed
  const year = parseInt(dateParts[2], 10);
  let hours = parseInt(timeParts[0], 10);
  const minutes = parseInt(timeParts[1], 10);

  // Convert to 24-hour format
  if (ampm === 'PM' && hours !== 12) hours += 12;
  if (ampm === 'AM' && hours === 12) hours = 0;

  return new Date(year, month, day, hours, minutes, 0);
}

const registrationDate = parseDatetime(datetime);

// Send 5 hours after registration
const welcomeAt = addHours(registrationDate, 5);
return toTimestamp(welcomeAt);

