const patient = payload.patient || {};
const visit = payload.visit || {};

const mrNumber = typeof patient.mrn === 'object' && patient.mrn !== null ? patient.mrn.documentNumber : patient.mrn;
const leadNo = visit.leadNo || visit.visitLeadNo || visit.leadNumber || payload.leadNo || null;
if (!leadNo) throw new Error('Missing lead_no');
if (!patient.fullName) throw new Error('Missing patient.fullName');
if (!mrNumber) throw new Error('Missing patient MRN');

function formatDateToDDMMYYYY(dateStr) {
  if (!dateStr) return null;
  if (/^\d{2}\/\d{2}\/\d{4}$/.test(dateStr)) return dateStr;
  if (/^\d{4}-\d{2}-\d{2}$/.test(dateStr)) {
    const [yyyy, mm, dd] = dateStr.split('-');
    return `${dd}/${mm}/${yyyy}`;
  }
  return null;
}

const details = {
  lead_status: '7',
  mr_number: mrNumber,
  medics_patient_name: patient.fullName
};
if (patient.email && patient.email.trim() !== '') details.email_id = patient.email;
if (patient.phone) details.mobile_no = patient.phone;
const visitDate = formatDateToDDMMYYYY(visit.date);
if (visitDate) details.visit_date = visitDate;
if (visit.typeName) details.visit_type = visit.typeName;
const remarks = visit.leadRemarks || visit.visitLeadRemarks || '';
if (remarks.trim() !== '') details.remarks = remarks;

return { lead_no: leadNo, details };
